def test():
  results = {
    'success': 0,
    'failure': [],
    'b_success': 0,
    'b_failure': [],
  }
  for case in TESTS:
    result, expected, b_result, b_expected = solve(case), TESTS[case][0], basement(case), TESTS[case][1]
    if result == expected:
      results['success'] += 1
    else:
      results['failure'].append((case, expected, result))
    if b_result == b_expected:
      results['b_success'] += 1
    else:
      results['b_failure'].append((case, b_expected, b_result))

  return results

def solve(input=DEFAULT_INPUT):
  floor = 0
  for char in input:
    if char == '(':
      floor += 1
      continue
    floor -= 1 # Yes, I know other characters could sneak in and break things.
  return floor

def basement(input=DEFAULT_INPUT):
  pass

if __name__ == '__main__':
  argv = sys.argv[1:]
  print(argv)

  if argv:
    command = argv[0].lower()
    if command == 'test':
      tests = test()
      for key in tests:
        print(key, tests[key])
    if command == 'basement':
      print(basement())
  
  print(solve())
  